DROP TABLE IF EXISTS `mcc_template`;
CREATE TABLE `mcc_template`  (
  `ID` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '模板id',
  `VERSION` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '版本',
  `P_ID` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '对应父级id',
  `SOURCE` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '来源(0:探矿权1：采矿权)',
  `NAME` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '模板名称',
  `LABLE` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '标签（多个用,隔开）',
  `KEYWORDS` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '关键字',
  `CONTENT` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '内容',
  `URL` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '地址',
  `REMARK` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '备注',
  `AUDIT_STATUS` decimal(6, 0) NULL DEFAULT NULL COMMENT '审核状态(-1:拒绝0：未审核1：同意)',
  `AUDIT_TIME` datetime NULL DEFAULT NULL COMMENT '审核时间',
  `AUDIT_USER` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '审核人',
  `FREQUENCY` decimal(6, 0) NULL DEFAULT NULL COMMENT '使用频率',
  `SORT` decimal(65, 30) NULL DEFAULT NULL COMMENT '排序',
  `EFFECTIVE_STATUS` decimal(6, 0) NULL DEFAULT NULL COMMENT '是否有效（0：无效1：有效）',
  `IS_HISTORY` decimal(6, 0) NULL DEFAULT NULL COMMENT '是否是历史数据（0不是1是）',
  `CREATE_USER` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '创建人',
  `CREATE_TIME` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `MODIFY_USER` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '修改人',
  `MODIFY_TIME` datetime NULL DEFAULT NULL COMMENT '修改时间',
  `DEL_STATUS` decimal(6, 0) NULL DEFAULT NULL COMMENT '删除状态',
  `DEL_USER` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '删除人',
  `DEL_TIME` datetime NULL DEFAULT NULL COMMENT '删除时间',
  `TYPE` decimal(6, 0) NULL DEFAULT NULL COMMENT '模板类型(0年度1：月度2：季度)',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '模板' ROW_FORMAT = DYNAMIC;

