INSERT INTO `mct_contract_tlp_his` VALUES ('97386f3f866d436bbe9b77926476eb7b', '1.0', '1', '探矿权合同模板(编辑)', 'ced9c09818d2415ebddbe0560fda9fd9', NULL, '<p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">合同编号：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{合同编号}}</u></span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><br></p><p style=\"text-align: center;\"><span style=\"font-size: 24px; font-family: 宋体;\"><u>({{项目名称}})</u></span></p><p style=\"text-align: center;\"><span style=\"font-size: 29px; font-family: 宋体;\"><strong>探</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>矿</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>权</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>出</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>让</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>合</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>同</strong></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(临时示范文本)</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-fami