INSERT INTO `mct_contract_tlp_his` VALUES ('ccce98c9b9384cf387ec4d6a8f3d0333', '1.1', '1', '探矿权合同模板(编辑)', 'ced9c09818d2415ebddbe0560fda9fd9', NULL, '<p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">合同编号：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{合同编号}}</u></span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><br></p><p style=\"text-align: center;\"><span style=\"font-size: 24px; font-family: 宋体;\"><u>({{项目名称}})</u></span></p><p style=\"text-align: center;\"><span style=\"font-size: 29px; font-family: 宋体;\"><strong>探</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>矿</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>权</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>出</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>让</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>合</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 29px; font-family: 宋体;\"> </span><span style=\"font-size: 29px; font-family: 宋体;\"><strong>同</strong></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(临时示范文本)</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">甲 &nbsp; &nbsp; &nbsp;方(出让人): </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{甲方}} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">住 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;所：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{甲方住所}} &nbsp; &nbsp; &nbsp; </u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">法 &nbsp;定 &nbsp;代 &nbsp;表 &nbsp;人：</span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"><u> </u></span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{甲方法定代表人}}</u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">乙 &nbsp; &nbsp;方(受让人): </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{乙方}} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">统一社会信用代码：</span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"><u> </u></span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{统一社会信用代码}}</u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">住 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;所：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{乙方住所}} &nbsp; &nbsp; &nbsp; &nbsp; </u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">法 &nbsp;定 &nbsp;代 表 人：</span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"><u> </u></span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{乙方法定代表人}} &nbsp;</u></span></p><p><br></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-indent: 32.45pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">根据《中华人民共和国矿产资源法》《矿业权出让收益征收办 法》等相关规定，甲乙双方经协商一致订立本合同。</span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong> &nbsp; &nbsp;第一条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;探矿权基本情况</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(一)勘查项目名称：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{勘查项目名称}} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</u></span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(二)勘查矿种：</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{勘查矿种}} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</u></span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(三)地理位置(所在行政区域): </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{地理位置}} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</u></span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;( 四 ) 面 &nbsp; &nbsp;积： </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{面积}} &nbsp; &nbsp;</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">平方千米</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(五)勘查区域坐标 </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> ： &nbsp;( 可 另 附 页 ) &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </u></span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><u> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">( 2 0 0 0 国 家 大 地 坐 标 系 )</span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong> &nbsp; &nbsp;第二条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> 出让方式： </span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{出让方式}}□招标□拍卖□挂牌□协议</u></span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong> &nbsp; &nbsp;第三条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;探矿权的期限为五年。探矿权期限届满，可以续期， 续期最多不超过三次，每次期限为五年。法律、行政法规另有规 &nbsp;定的除外。</span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong> &nbsp; &nbsp;第四条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;矿业权出让收益</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(以下仅用于按出让收益率形式征收)</span></p><p style=\"text-indent: 35.95pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(一)一次性缴纳探矿权成交价部分为人民币</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{一次性缴纳(大写)}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">元 (大 写) (￥ </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{一次性缴纳(小写)}} </u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">元)。乙方应在收到征收机关缴款通知书之日 起30日内缴纳。</span></p><p style=\"text-indent: 35.95pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(二)该探矿权转为采矿权后，按照矿产品销售收入的</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{销售收入}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> % 逐年向征收机关申报缴纳采矿权出让收益，缴款时间等事项在采矿权出让合同中按规定另行约定。(仅用于招标/拍卖/挂牌出让的 探矿权)</span></p><p style=\"text-indent: 35.95pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">该探矿权转为采矿权后，按照矿产品销售时的矿业权出让收 益率逐年向征收机关申报缴纳采矿权出让收益，缴款时间等事项 在采矿权出让合同中按规定另行约定。 (仅用于协议出让的探矿权 )</span></p><p style=\"text-indent: 30.45pt; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">在转为采矿权前，乙方发现油气资源并进行开采、产生收入 时，应按照矿产品销售收入的</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{销售收入(油气)}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> %逐年缴纳矿业权出让收益。(仅用于油气探矿权出让)</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(以下仅用于按出让金额形式征收)</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(一)探矿权出让收益为人民币</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{原探矿权出让收益(大写)}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> 元(大写) (￥</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{原探矿权出让收益(小写)}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">元 ) 。</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;(二)探矿权出让收益按以下第</span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{缴纳方式}} </u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">种方式缴纳：</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;1.一次性缴清。乙方应在收到征收机关缴款通知书之日起30 日内缴纳。</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp; &nbsp;2.分期缴纳。首次缴纳人民币 </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{首次缴纳人民币大写}} &nbsp; &nbsp; &nbsp; &nbsp;</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> 元 (大写) (￥ </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> {{首次缴纳人民币小写}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">元),乙方应在收到征收机关缴款通知书之日起30 日内缴纳。剩余部分在该探矿权转为采矿权后缴纳，分期缴纳时间和期限等事项在采矿权出让合同中按规定另行约定；未转为采 矿权的，剩余部分不再缴纳。</span></p><p style=\"text-indent: 30.45pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">探矿权转为采矿权时如变更矿种，应在采矿权出让合同中约 定采矿权出让收益缴纳事宜。</span></p><p style=\"text-indent: 30.15pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第五条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方未按时足额缴纳矿业权出让收益的，从违约之 日起每日加收千分之二的违约金，加收的违约金不超过欠缴金额 本金。</span></p><p style=\"text-indent: 30.2pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第六条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方应按规定签订互不影响和权益保护协议或出具 不影响已设矿业权人权益承诺，严格执行相关安全要求。(仅用于 勘查区域与已设矿业权范围重叠)</span></p><p style=\"text-indent: 30.2pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第七条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方自取得缴款凭证之日起15个工作日内，应向甲 方申请办理探矿权登记。</span></p><p style=\"text-indent: 30.15pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第八条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;甲方对于乙方提交申请材料符合要求的探矿权登记 申请，应当自受理登记申请之日起20个工作日内办结探矿权登记 手续。</span></p><p style=\"text-indent: 29.95pt; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">本合同签订生效后，甲方不应将本合同约定的全部或者部分 勘查区域另行向第三方出让探矿权或者采矿权，依据相关规定同 一区域可以按照不同矿种分别设置探矿权、采矿权的情形除外。</span></p><p style=\"text-indent: 30.2pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第九条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方自本合同签订之日起 </span><span style=\"font-size: 24px; font-family: 宋体;\"><u>6 个 月 内</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">，应编制勘查方 案，申请办理勘查许可。未取得勘查许可证，不得进行矿产资源 勘查作业。</span></p><p style=\"text-indent: 30.2pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> 乙方申请探矿权续期时，需按照相关规定核减勘查 面积，勘查区域以探矿权登记为准，符合规定的特殊情形除外。</span></p><p style=\"text-indent: 32.45pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">乙方应当及时开展勘查工作，无正当理由未开展或者未实质 性开展勘查工作的，探矿权期限届满时不予续期。</span></p><p style=\"text-indent: 32.45pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十一条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> 乙方按规定可以转让探矿权，本合同约定的权利、义务依法随之转移。</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>(出让合同另有约定不得转让的情形)</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">不得转让 。</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">乙方转让以协议方式取得的探矿权，应当持有探矿权满5年， 符合规定的特殊情形除外。(仅用于协议方式出让的探矿权)</span></p><p style=\"text-indent: 31.2pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十二条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方在持有探矿权期间，应当严格遵守矿产资源 &nbsp;法律法规、相关矿业权管理政策，认真履行综合勘查、绿色勘查、地质资料汇交、合理和集约节约用地用林用草用海、临时用地恢复、探矿权占用费缴纳、矿产资源应急处置</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{应急处置}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;等相关义务。勘查活动结束后，应当履行清理、回填、封堵、植被恢复等义务 。</span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong> &nbsp; &nbsp;第十三条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;乙方探明可供开采的矿产资源后，可以在探矿权期限内申请将其探矿权转为采矿权，且应符合保护性开采的有关要求(仅用于特定战略性矿产资源),法律、行政法规另有规定的 除外 。</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">乙方发现可供开采的油气资源的，按规定开展油气探采合一 工作。(仅用于油气探矿权)</span></p><p style=\"text-indent: 35pt; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">因公共利益的需要，或者因不可抗力或者其他特殊情形，探 矿权暂时不能转为采矿权的，乙方可以申请办理探矿权保留。探 矿权保留期间，探矿权期限中止计算。</span></p><p><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十四条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;有下列情形之一的，本合同自动解除：</span></p><p style=\"text-indent: 40.5pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(一)乙方因自身原因，逾期</span><span style=\"font-size: 24px; font-family: 宋体;\"><u>{{逾期}}</u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">日未足额缴纳矿业权出让收 益成交价部分的(仅用于按出让收益率形式征收),探矿权出让收益的(仅用于按出让金额形式征收且一次性缴纳), &nbsp;首期探矿权出 让收益的(仅用于按出让金额形式征收且分期缴纳);</span></p><p style=\"text-indent: 36.95pt;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(二)乙方因自身原因，未按照本合同约定向甲方申请办理 探矿权首次登记的；</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">(三)探矿权消灭的 (探矿权转为采矿权的除外)。</span></p><p style=\"text-indent: 29.5pt; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">合同解除后，甲方按规定处置矿业权出让收益、探矿权登记 等相关事宜。为了公共利益需要，矿业权被收回的，应当依法给 予公平、合理的补偿。</span></p><p style=\"text-indent: 29.7pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十五条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;本合同未尽事宜，双方经协商一致后，可另行签 订补充协议。补充协议与本合同具有同等法律效力。</span></p><p style=\"text-indent: 29.5pt; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">本合同及补充协议没有约定的，应当按照相关法律法规和规 章、政策规定执行。本合同及补充协议签订后，法律法规和规章、 政策规定发生变化的，按变化后的规定执行。</span></p><p style=\"text-indent: 29.7pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十六条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;本合同及补充协议履行过程中双方发生争议的， 应当协商解决；协商不成的，甲乙双方有权向甲方所在地有管辖 权的人民法院提起行政诉讼。</span></p><p style=\"text-indent: 29.7pt;\"><span style=\"font-size: 24px; font-family: 宋体;\"><strong>第十七条</strong></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> &nbsp;本合同一式六份，甲乙双方各持三份，自双方盖 章之日起生效。</span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">甲 方(盖章): </span><span style=\"font-size: 24px; font-family: 宋体;\"><u> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </u></span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"> </span></p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">时 &nbsp; &nbsp; &nbsp;间： </span><span style=\"font-size: 24px; font-family: 宋体;\"><u><strong> &nbsp; &nbsp; &nbsp;</strong></u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">年</span><span style=\"font-size: 24px; font-family: 宋体;\"><u><strong> &nbsp; &nbsp; </strong></u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">月</span><span style=\"font-size: 24px; font-family: 宋体;\"><u><strong> &nbsp; &nbsp;</strong></u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\"><u><strong> </strong></u></span><span style=\"color: rgb(0, 0, 0); font-size: 24px; font-family: 宋体;\">日</span></p><p style=\"tex