  `length` decimal(65, 0) NULL DEFAULT NULL COMMENT '长度',
  `constraint` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '约束条件（多个用逗号隔开）',
