  `audit_status` decimal(65, 0) NULL DEFAULT NULL COMMENT '审核状态(-1:拒绝0：未审核1：同意)',
  `audit_time` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '审核时间',
